package tc.oc.pgm.modules;

import com.mojang.authlib.properties.Property;
import java.util.Arrays;
import java.util.List;
import org.bukkit.Skin;
import org.bukkit.event.EventHandler;
import org.bukkit.event.EventPriority;
import org.bukkit.event.Listener;
import tc.oc.pgm.api.match.Match;
import tc.oc.pgm.api.match.MatchModule;
import tc.oc.pgm.api.match.MatchScope;
import tc.oc.pgm.events.ListenerScope;
import tc.oc.pgm.events.PlayerPartyChangeEvent;

@ListenerScope(MatchScope.LOADED)
public class SteveSkinsMatchModule implements MatchModule, Listener {

  private static final List<Property> coloredSkins =
      Arrays.asList(
          /*0, black*/ new Property(
              "textures",
              "",
              ""),
          /*1, dark blue*/ new Property(
              "textures",
              "",
              ""),
          /*2, dark green*/ new Property(
              "textures",
              "",
              ""),
          /*3, dark aqua*/ new Property(
              "textures",
              "",
              ""),
          /*4, dark red*/ new Property(
              "textures",
              "",
              ""),
          /*5, dark purple*/ new Property(
              "textures",
              "",
              ""),
          /*6, gold*/ new Property(
              "textures",
              "",
              ""),
          /*7, gray*/ new Property(
              "textures",
              "",
              ""),
          /*8, dark gray*/ new Property(
              "textures",
              "",
              ""),
          /*9, blue*/ new Property(
              "textures",
              "",
              ""),
          /*a, green*/ new Property(
              "textures",
              "",
              ""),
          /*b, aqua*/ new Property(
              "textures",
              "",
              ""),
          /*c, red*/ new Property(
              "textures",
              "",
              ""),
          /*d, light purple*/ new Property(
              "textures",
              "",
              ""),
          /*e, yellow*/ new Property(
              "textures",
              "",
              ""),
          /*f, white*/ new Property(
              "textures",
              "",
              ""));

  public SteveSkinsMatchModule(Match match) {}

  @EventHandler(priority = EventPriority.HIGHEST, ignoreCancelled = true)
  public void onPlayerPartyChange(final PlayerPartyChangeEvent event) {
    Property property =
        coloredSkins.get(
            event.getNewParty() != null ? event.getNewParty().getColor().ordinal() : 11);
    Skin skin = new Skin(property.getValue(), property.getSignature());
    event.getPlayer().setSkin(skin);
  }
}
